     1â†’import express from 'express';
     2â†’import cors from 'cors';
     3â†’import helmet from 'helmet';
     4â†’import rateLimit from 'express-rate-limit';
     5â†’import dotenv from 'dotenv';
     6â†’import { errorHandler } from './middleware/errorHandler';
     7â†’import { logger } from './utils/logger';
     8â†’import { prisma } from './utils/database';
     9â†’import authRoutes from './routes/auth';
    10â†’import userRoutes from './routes/users';
    11â†’import repositoryRoutes from './routes/repositories';
    12â†’import analysisRoutes from './routes/analysis';
    13â†’import subscriptionRoutes from './routes/subscriptions';
    14â†’import webhookRoutes from './routes/webhooks';
    15â†’
    16â†’dotenv.config();
    17â†’
    18â†’const app = express();
    19â†’const PORT = process.env.PORT || 3000;
    20â†’
    21â†’// Trust proxy for accurate IP addresses
    22â†’app.set('trust proxy', 1);
    23â†’
    24â†’// Security middleware
    25â†’app.use(helmet());
    26â†’
    27â†’// CORS configuration
    28â†’app.use(cors({
    29â†’  origin: process.env.FRONTEND_URL || 'http://localhost:5173',
    30â†’  credentials: true,
    31â†’  methods: ['GET', 'POST', 'PUT', 'DELETE', 'PATCH'],
    32â†’  allowedHeaders: ['Content-Type', 'Authorization', 'X-Requested-With']
    33â†’}));
    34â†’
    35â†’// Rate limiting
    36â†’const limiter = rateLimit({
    37â†’  windowMs: 15 * 60 * 1000, // 15 minutes
    38â†’  max: 100, // limit each IP to 100 requests per windowMs
    39â†’  message: 'Too many requests from this IP, please try again later.',
    40â†’  standardHeaders: true,
    41â†’  legacyHeaders: false,
    42â†’});
    43â†’
    44â†’app.use(limiter);
    45â†’
    46â†’// Body parsing middleware
    47â†’app.use(express.json({ limit: '10mb' }));
    48â†’app.use(express.urlencoded({ extended: true, limit: '10mb' }));
    49â†’
    50â†’// Logging middleware
    51â†’app.use((req, res, next) => {
    52â†’  logger.info(`${req.method} ${req.path}`, {
    53â†’    ip: req.ip,
    54â†’    userAgent: req.get('user-agent'),
    55â†’    timestamp: new Date().toISOString()
    56â†’  });
    57â†’  next();
    58â†’});
    59â†’
    60â†’// Health check endpoint
    61â†’app.get('/health', (req, res) => {
    62â†’  res.json({
    63â†’    status: 'healthy',
    64â†’    timestamp: new Date().toISOString(),
    65â†’    uptime: process.uptime(),
    66â†’    environment: process.env.NODE_ENV || 'development'
    67â†’  });
    68â†’});
    69â†’
    70â†’// API routes
    71â†’app.use('/api/auth', authRoutes);
    72â†’app.use('/api/users', userRoutes);
    73â†’app.use('/api/repositories', repositoryRoutes);
    74â†’app.use('/api/analysis', analysisRoutes);
    75â†’app.use('/api/subscriptions', subscriptionRoutes);
    76â†’app.use('/api/webhooks', webhookRoutes);
    77â†’
    78â†’// 404 handler
    79â†’app.use((req, res) => {
    80â†’  res.status(404).json({
    81â†’    error: 'Not Found',
    82â†’    message: `Route ${req.method} ${req.path} not found`,
    83â†’    timestamp: new Date().toISOString()
    84â†’  });
    85â†’});
    86â†’
    87â†’// Global error handler
    88â†’app.use(errorHandler);
    89â†’
    90â†’// Graceful shutdown
    91â†’process.on('SIGTERM', async () => {
    92â†’  logger.info('SIGTERM received, starting graceful shutdown...');
    93â†’  
    94â†’  // Close database connection
    95â†’  await prisma.$disconnect();
    96â†’  
    97â†’  logger.info('Graceful shutdown completed');
    98â†’  process.exit(0);
    99â†’});
   100â†’
   101â†’// Start server
   102â†’const startServer = async () => {
   103â†’  try {
   104â†’    // Test database connection
   105â†’    await prisma.$connect();
   106â†’    logger.info('Database connected successfully');
   107â†’
   108â†’    app.listen(PORT, () => {
   109â†’      logger.info(`Server running on port ${PORT}`, {
   110â†’        environment: process.env.NODE_ENV || 'development',
   111â†’        port: PORT
   112â†’      });
   113â†’    });
   114â†’  } catch (error) {
   115â†’    logger.error('Failed to start server:', error);
   116â†’    process.exit(1);
   117â†’  }
   118â†’};
   119â†’
   120â†’startServer();
   121â†’
   122â†’export default app;

