     1â†’import React from 'react';
     2â†’import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
     3â†’import { ReactQueryDevtools } from '@tanstack/react-query-devtools';
     4â†’import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
     5â†’import { AuthProvider } from './contexts/AuthContext';
     6â†’import { useAuth } from './hooks/useAuth';
     7â†’import LandingPage from './pages/LandingPage';
     8â†’import LoginPage from './pages/LoginPage';
     9â†’import DashboardLayout from './components/layouts/DashboardLayout';
    10â†’import Dashboard from './pages/Dashboard';
    11â†’import RepositoriesPage from './pages/RepositoriesPage';
    12â†’import RepositoryDetailPage from './pages/RepositoryDetailPage';
    13â†’import SettingsPage from './pages/SettingsPage';
    14â†’import PricingPage from './pages/PricingPage';
    15â†’import LoadingSpinner from './components/ui/LoadingSpinner';
    16â†’
    17â†’// Create a client
    18â†’const queryClient = new QueryClient({
    19â†’  defaultOptions: {
    20â†’    queries: {
    21â†’      staleTime: 5 * 60 * 1000, // 5 minutes
    22â†’      retry: (failureCount, error: any) => {
    23â†’        if (error?.status === 401) return false;
    24â†’        return failureCount < 3;
    25â†’      }
    26â†’    }
    27â†’  }
    28â†’});
    29â†’
    30â†’const ProtectedRoute: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    31â†’  const { user, isLoading } = useAuth();
    32â†’
    33â†’  if (isLoading) {
    34â†’    return (
    35â†’      <div className="min-h-screen flex items-center justify-center">
    36â†’        <LoadingSpinner size="lg" />
    37â†’      </div>
    38â†’    );
    39â†’  }
    40â†’
    41â†’  if (!user) {
    42â†’    return <Navigate to="/login" replace />;
    43â†’  }
    44â†’
    45â†’  return <>{children}</>;
    46â†’};
    47â†’
    48â†’const PublicRoute: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    49â†’  const { user, isLoading } = useAuth();
    50â†’
    51â†’  if (isLoading) {
    52â†’    return (
    53â†’      <div className="min-h-screen flex items-center justify-center">
    54â†’        <LoadingSpinner size="lg" />
    55â†’      </div>
    56â†’    );
    57â†’  }
    58â†’
    59â†’  if (user) {
    60â†’    return <Navigate to="/dashboard" replace />;
    61â†’  }
    62â†’
    63â†’  return <>{children}</>;
    64â†’};
    65â†’
    66â†’function AppRoutes() {
    67â†’  return (
    68â†’    <Routes>
    69â†’      {/* Public routes */}
    70â†’      <Route 
    71â†’        path="/" 
    72â†’        element={
    73â†’          <PublicRoute>
    74â†’            <LandingPage />
    75â†’          </PublicRoute>
    76â†’        } 
    77â†’      />
    78â†’      <Route 
    79â†’        path="/login" 
    80â†’        element={
    81â†’          <PublicRoute>
    82â†’            <LoginPage />
    83â†’          </PublicRoute>
    84â†’        } 
    85â†’      />
    86â†’      <Route path="/pricing" element={<PricingPage />} />
    87â†’
    88â†’      {/* Protected routes */}
    89â†’      <Route 
    90â†’        path="/dashboard" 
    91â†’        element={
    92â†’          <ProtectedRoute>
    93â†’            <DashboardLayout>
    94â†’              <Dashboard />
    95â†’            </DashboardLayout>
    96â†’          </ProtectedRoute>
    97â†’        } 
    98â†’      />
    99â†’      <Route 
   100â†’        path="/repositories" 
   101â†’        element={
   102â†’          <ProtectedRoute>
   103â†’            <DashboardLayout>
   104â†’              <RepositoriesPage />
   105â†’            </DashboardLayout>
   106â†’          </ProtectedRoute>
   107â†’        } 
   108â†’      />
   109â†’      <Route 
   110â†’        path="/repositories/:id" 
   111â†’        element={
   112â†’          <ProtectedRoute>
   113â†’            <DashboardLayout>
   114â†’              <RepositoryDetailPage />
   115â†’            </DashboardLayout>
   116â†’          </ProtectedRoute>
   117â†’        } 
   118â†’      />
   119â†’      <Route 
   120â†’        path="/settings" 
   121â†’        element={
   122â†’          <ProtectedRoute>
   123â†’            <DashboardLayout>
   124â†’              <SettingsPage />
   125â†’            </DashboardLayout>
   126â†’          </ProtectedRoute>
   127â†’        } 
   128â†’      />
   129â†’
   130â†’      {/* Catch all route */}
   131â†’      <Route path="*" element={<Navigate to="/" replace />} />
   132â†’    </Routes>
   133â†’  );
   134â†’}
   135â†’
   136â†’function App() {
   137â†’  return (
   138â†’    <QueryClientProvider client={queryClient}>
   139â†’      <Router>
   140â†’        <AuthProvider>
   141â†’          <div className="min-h-screen bg-gray-50">
   142â†’            <AppRoutes />
   143â†’          </div>
   144â†’        </AuthProvider>
   145â†’      </Router>
   146â†’      <ReactQueryDevtools initialIsOpen={false} />
   147â†’    </QueryClientProvider>
   148â†’  );
   149â†’}
   150â†’
   151â†’export default App;
   152â†’

