     1â†’import { Router } from 'express';
     2â†’import { authenticateToken, AuthenticatedRequest } from '../middleware/auth';
     3â†’import { prisma } from '../utils/database';
     4â†’import { createError } from '../middleware/errorHandler';
     5â†’import { logger } from '../utils/logger';
     6â†’
     7â†’const router = Router();
     8â†’
     9â†’// Get user profile
    10â†’router.get('/profile', authenticateToken, async (req: AuthenticatedRequest, res) => {
    11â†’  try {
    12â†’    const user = await prisma.user.findUnique({
    13â†’      where: { id: req.user!.id },
    14â†’      select: {
    15â†’        id: true,
    16â†’        email: true,
    17â†’        name: true,
    18â†’        avatar: true,
    19â†’        createdAt: true,
    20â†’        updatedAt: true,
    21â†’        subscription: {
    22â†’          select: {
    23â†’            plan: true,
    24â†’            status: true,
    25â†’            maxRepositories: true,
    26â†’            maxAnalysesPerMonth: true,
    27â†’            repositoriesUsed: true,
    28â†’            analysesThisMonth: true,
    29â†’            currentPeriodEnd: true
    30â†’          }
    31â†’        }
    32â†’      }
    33â†’    });
    34â†’
    35â†’    if (!user) {
    36â†’      throw createError('User not found', 404);
    37â†’    }
    38â†’
    39â†’    res.json({ user });
    40â†’  } catch (error) {
    41â†’    logger.error('Failed to fetch user profile:', error);
    42â†’    res.status(500).json({ error: 'Failed to fetch user profile' });
    43â†’  }
    44â†’});
    45â†’
    46â†’// Update user profile
    47â†’router.put('/profile', authenticateToken, async (req: AuthenticatedRequest, res) => {
    48â†’  try {
    49â†’    const { name, email } = req.body;
    50â†’
    51â†’    // Validate input
    52â†’    if (!name || name.length < 2) {
    53â†’      throw createError('Name must be at least 2 characters', 400);
    54â†’    }
    55â†’
    56â†’    if (!email || !email.includes('@')) {
    57â†’      throw createError('Valid email is required', 400);
    58â†’    }
    59â†’
    60â†’    // Check if email is already taken by another user
    61â†’    if (email !== req.user!.email) {
    62â†’      const existingUser = await prisma.user.findUnique({
    63â†’        where: { email }
    64â†’      });
    65â†’
    66â†’      if (existingUser) {
    67â†’        throw createError('Email is already taken', 409);
    68â†’      }
    69â†’    }
    70â†’
    71â†’    const updatedUser = await prisma.user.update({
    72â†’      where: { id: req.user!.id },
    73â†’      data: { name, email },
    74â†’      select: {
    75â†’        id: true,
    76â†’        email: true,
    77â†’        name: true,
    78â†’        avatar: true,
    79â†’        updatedAt: true
    80â†’      }
    81â†’    });
    82â†’
    83â†’    logger.info('User profile updated', { userId: req.user!.id });
    84â†’
    85â†’    res.json({ user: updatedUser });
    86â†’  } catch (error) {
    87â†’    logger.error('Failed to update user profile:', error);
    88â†’    res.status(500).json({ error: 'Failed to update user profile' });
    89â†’  }
    90â†’});
    91â†’
    92â†’// Get user statistics
    93â†’router.get('/stats', authenticateToken, async (req: AuthenticatedRequest, res) => {
    94â†’  try {
    95â†’    const [
    96â†’      repositoryCount,
    97â†’      analysisCount,
    98â†’      recentAnalyses,
    99â†’      subscription
   100â†’    ] = await Promise.all([
   101â†’      prisma.repository.count({
   102â†’        where: { userId: req.user!.id }
   103â†’      }),
   104â†’      prisma.analysisResult.count({
   105â†’        where: { userId: req.user!.id }
   106â†’      }),
   107â†’      prisma.analysisResult.findMany({
   108â†’        where: { userId: req.user!.id },
   109â†’        orderBy: { analyzedAt: 'desc' },
   110â†’        take: 5,
   111â†’        select: {
   112â†’          id: true,
   113â†’          analyzedAt: true,
   114â†’          overallScore: true,
   115â†’          criticalIssues: true,
   116â†’          majorIssues: true,
   117â†’          repository: {
   118â†’            select: {
   119â†’              name: true,
   120â†’              language: true
   121â†’            }
   122â†’          }
   123â†’        }
   124â†’      }),
   125â†’      prisma.subscription.findFirst({
   126â†’        where: { userId: req.user!.id },
   127â†’        select: {
   128â†’          plan: true,
   129â†’          maxRepositories: true,
   130â†’          maxAnalysesPerMonth: true,
   131â†’          repositoriesUsed: true,
   132â†’          analysesThisMonth: true
   133â†’        }
   134â†’      })
   135â†’    ]);
   136â†’
   137â†’    // Calculate average score from recent analyses
   138â†’    const avgScore = recentAnalyses.length > 0 
   139â†’      ? recentAnalyses.reduce((sum, analysis) => sum + analysis.overallScore, 0) / recentAnalyses.length
   140â†’      : 0;
   141â†’
   142â†’    // Count total issues
   143â†’    const totalIssues = recentAnalyses.reduce((sum, analysis) => 
   144â†’      sum + analysis.criticalIssues + analysis.majorIssues, 0
   145â†’    );
   146â†’
   147â†’    res.json({
   148â†’      stats: {
   149â†’        repositories: {
   150â†’          total: repositoryCount,
   151â†’          limit: subscription?.maxRepositories || 0,
   152â†’          remaining: Math.max(0, (subscription?.maxRepositories || 0) - repositoryCount)
   153â†’        },
   154â†’        analyses: {
   155â†’          total: analysisCount,
   156â†’          thisMonth: subscription?.analysesThisMonth || 0,
   157â†’          limit: subscription?.maxAnalysesPerMonth || 0,
   158â†’          remaining: Math.max(0, (subscription?.maxAnalysesPerMonth || 0) - (subscription?.analysesThisMonth || 0))
   159â†’        },
   160â†’        quality: {
   161â†’          averageScore: Math.round(avgScore * 10) / 10,
   162â†’          totalIssues,
   163â†’          recentAnalyses: recentAnalyses.length
   164â†’        }
   165â†’      },
   166â†’      recentAnalyses
   167â†’    });
   168â†’  } catch (error) {
   169â†’    logger.error('Failed to fetch user statistics:', error);
   170â†’    res.status(500).json({ error: 'Failed to fetch user statistics' });
   171â†’  }
   172â†’});
   173â†’
   174â†’// Get user activity feed
   175â†’router.get('/activity', authenticateToken, async (req: AuthenticatedRequest, res) => {
   176â†’  try {
   177â†’    const { page = 1, limit = 20 } = req.query;
   178â†’    const offset = (Number(page) - 1) * Number(limit);
   179â†’
   180â†’    const activities = await prisma.analysisResult.findMany({
   181â†’      where: { userId: req.user!.id },
   182â†’      orderBy: { analyzedAt: 'desc' },
   183â†’      skip: offset,
   184â†’      take: Number(limit),
   185â†’      select: {
   186â†’        id: true,
   187â†’        analyzedAt: true,
   188â†’        analysisType: true,
   189â†’        overallScore: true,
   190â†’        criticalIssues: true,
   191â†’        majorIssues: true,
   192â†’        minorIssues: true,
   193â†’        commitSha: true,
   194â†’        branch: true,
   195â†’        repository: {
   196â†’          select: {
   197â†’            name: true,
   198â†’            fullName: true,
   199â†’            language: true
   200â†’          }
   201â†’        }
   202â†’      }
   203â†’    });
   204â†’
   205â†’    const totalActivities = await prisma.analysisResult.count({
   206â†’      where: { userId: req.user!.id }
   207â†’    });
   208â†’
   209â†’    res.json({
   210â†’      activities,
   211â†’      pagination: {
   212â†’        page: Number(page),
   213â†’        limit: Number(limit),
   214â†’        total: totalActivities,
   215â†’        pages: Math.ceil(totalActivities / Number(limit))
   216â†’      }
   217â†’    });
   218â†’  } catch (error) {
   219â†’    logger.error('Failed to fetch user activity:', error);
   220â†’    res.status(500).json({ error: 'Failed to fetch user activity' });
   221â†’  }
   222â†’});
   223â†’
   224â†’// Delete user account
   225â†’router.delete('/account', authenticateToken, async (req: AuthenticatedRequest, res) => {
   226â†’  try {
   227â†’    const { confirmEmail } = req.body;
   228â†’
   229â†’    // Confirm email for security
   230â†’    if (confirmEmail !== req.user!.email) {
   231â†’      throw createError('Email confirmation does not match', 400);
   232â†’    }
   233â†’
   234â†’    // Delete in transaction to maintain data integrity
   235â†’    await prisma.$transaction(async (tx) => {
   236â†’      // Delete analysis results
   237â†’      await tx.analysisResult.deleteMany({
   238â†’        where: { userId: req.user!.id }
   239â†’      });
   240â†’
   241â†’      // Delete repositories
   242â†’      await tx.repository.deleteMany({
   243â†’        where: { userId: req.user!.id }
   244â†’      });
   245â†’
   246â†’      // Delete subscription
   247â†’      await tx.subscription.deleteMany({
   248â†’        where: { userId: req.user!.id }
   249â†’      });
   250â†’
   251â†’      // Delete user
   252â†’      await tx.user.delete({
   253â†’        where: { id: req.user!.id }
   254â†’      });
   255â†’    });
   256â†’
   257â†’    logger.info('User account deleted', { userId: req.user!.id });
   258â†’
   259â†’    res.json({
   260â†’      message: 'Account deleted successfully',
   261â†’      timestamp: new Date().toISOString()
   262â†’    });
   263â†’  } catch (error) {
   264â†’    logger.error('Failed to delete user account:', error);
   265â†’    res.status(500).json({ error: 'Failed to delete user account' });
   266â†’  }
   267â†’});
   268â†’
   269â†’// Export user data (GDPR compliance)
   270â†’router.get('/export', authenticateToken, async (req: AuthenticatedRequest, res) => {
   271â†’  try {
   272â†’    const user = await prisma.user.findUnique({
   273â†’      where: { id: req.user!.id },
   274â†’      include: {
   275â†’        repositories: {
   276â†’          include: {
   277â†’            analysisResults: {
   278â†’              orderBy: { analyzedAt: 'desc' }
   279â†’            }
   280â†’          }
   281â†’        },
   282â†’        subscription: true
   283â†’      }
   284â†’    });
   285â†’
   286â†’    if (!user) {
   287â†’      throw createError('User not found', 404);
   288â†’    }
   289â†’
   290â†’    // Remove sensitive data
   291â†’    const exportData = {
   292â†’      user: {
   293â†’        id: user.id,
   294â†’        email: user.email,
   295â†’        name: user.name,
   296â†’        avatar: user.avatar,
   297â†’        createdAt: user.createdAt,
   298â†’        updatedAt: user.updatedAt
   299â†’      },
   300â†’      subscription: user.subscription ? {
   301â†’        plan: user.subscription.plan,
   302â†’        status: user.subscription.status,
   303â†’        createdAt: user.subscription.createdAt
   304â†’      } : null,
   305â†’      repositories: user.repositories.map(repo => ({
   306â†’        id: repo.id,
   307â†’        name: repo.name,
   308â†’        fullName: repo.fullName,
   309â†’        description: repo.description,
   310â†’        language: repo.language,
   311â†’        createdAt: repo.createdAt,
